import { Container, Text, Image, Box, Flex, Button, Editable,
    EditableInput,
    EditableTextarea,
    EditablePreview, } from "@chakra-ui/react";
import styles from './profile.module.css';

function Profile(){
    
    return(
        <div>
            <Flex align="center" w="100%" justify="center">
                <Image 
                    boxSize='200px'
                    borderRadius='100px'
                    display='flex'
                    justifyContent='center'
                    src="https://preview.redd.it/7ayjc8s4j2n61.png?auto=webp&s=609a58fa21d46424879ee44156e44e0404940583" alt='default pic'
                />
                <Button>Change Profile Pic</Button>
                <CustomControls></CustomControls>
            </Flex>
            <Flex>

            </Flex>
        </div>
    )}

    function CustomControls() {
        /* Here's a custom control */
        function EditableControls() {
          const {
            isEditing,
            getSubmitButtonProps,
            getCancelButtonProps,
            getEditButtonProps,
          } = useEditableControls()
      
          return isEditing ? (
            <ButtonGroup justifyContent='center' size='sm'>
              <IconButton icon={<CheckIcon />} {...getSubmitButtonProps()} />
              <IconButton icon={<CloseIcon />} {...getCancelButtonProps()} />
            </ButtonGroup>
          ) : (
            <Flex justifyContent='center'>
              <IconButton size='sm' icon={<EditIcon />} {...getEditButtonProps()} />
            </Flex>
          )
        }
      
        return (
          <Editable
            textAlign='center'
            defaultValue='Rasengan ⚡️'
            fontSize='2xl'
            isPreviewFocusable={false}
          >
            <EditablePreview />
            {/* Here is the custom input */}
            <Input as={EditableInput} />
            <EditableControls />
          </Editable>
        )
      }
export default Profile;
